mapper.select:
select * from SensorData WHERE DATE(date) >= STR_TO_DATE(#{startDate}, '%Y-%m-%d') AND DATE(date) <= STR_TO_DATE(#{endDate}, '%Y-%m-%d')

        	AND Location = #{location}

order by date desc, time desc

